package com.notagain

import android.content.Context
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.ImageView
import androidx.recyclerview.widget.RecyclerView
import com.google.android.material.imageview.ShapeableImageView
import com.notagain.notagain.R
import com.squareup.picasso.Picasso

class CreatorAdapter(var context: Context, imageList: ArrayList<CreatorModel>) :
        RecyclerView.Adapter<CreatorAdapter .ViewHolder>() {


    var imageList: List<CreatorModel> = listOf() // empty product list


    inner class ViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {}

    //Note below code returns above class and pass the view
    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): CreatorAdapter.ViewHolder {
        val view = LayoutInflater.from(parent.context).inflate(R.layout.itemcreator, parent, false)
        return ViewHolder(view)
    }


    //so far item view is same as single item
    override fun onBindViewHolder(holder: CreatorAdapter.ViewHolder, position: Int) {
        val image = holder.itemView.findViewById(R.id.cthumbnail) as ImageView

        val item = imageList[position]
        if (item.image == "blank") {
            image.visibility = View.GONE
        }//ends the if
        else {
            println("my image is ="+item.image)
            Picasso
                .with(context)
                .load("data:image/jpeg;base64,")
                .fit()
                .into(image);

        }//ends the else
    }

    override fun getItemCount(): Int {
        return imageList.size
    }

    //we will call this function on Loopj response
    fun setImageListItems(imageList: List<CreatorModel>) {
        this.imageList = imageList
        notifyDataSetChanged()
    }


}//ends class